mapper.select:
SELECT *
FROM   MEMBER
WHERE  id = #{id}
AND    password = #{password}
AND    status != 'DELETE'

mapper.update:
UPDATE MEMBER
SET password = #{password}
WHERE id=#{id}

UPDATE MEMBER
SET status = 'DELETE'
WHERE id=#{id}

UPDATE MEMBER
SET address_code = #{addressCode},
    address_detail = #{addressDetail}
WHERE id = #{id}

mapper.select:
SELECT COUNT(id)
  FROM MEMBER
 WHERE id = #{id}

SELECT  SUBSTRING(address_code, 1, 10)
FROM    MEMBER
WHERE   id = #{id}
