mapper.select:
SELECT id, password, name, mail, tel, created_at createdAt, updated_at updatedAt, status
FROM OWNER
WHERE id = #{id}
 AND password = #{password}

SELECT id, password, name, mail, tel, created_at createdAt, updated_at updatedAt, status
FROM OWNER
WHERE id = #{id}

mapper.update:
UPDATE OWNER
SET password = #{password}
WHERE id = #{id}
